package algorithm.dynamicprogramming;

import java.util.Arrays;

public class LCSPrac {

    int[][] dp;
    int count;
    //String s1="";
    //String s2="";

    String s1="cccccc";//ccccccccccccccccccccccccccccccccccccc";";
    String s2="bbbbbb";//bbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbb";";

    public int longestCommonSubsequence() {
        dp = new int[s1.length()][s2.length()];
        //for (int[] a: dp) Arrays.fill(a,-1);
        return longestCommonSubseq(s1.length() - 1,s2.length() - 1);
    }

    public int longestCommonSubseq(int i, int j) {
        count++;
        System.out.println("i= "+i+", j= "+j);
        if(i < 0 || j < 0) return 0;
        if(dp[i][j]!=0) return dp[i][j];
        if (s1.charAt(i) == s2.charAt(j)) {
            dp[i][j] = 1 + longestCommonSubseq(i - 1, j - 1);
        } else {
            dp[i][j] = Math.max(longestCommonSubseq(i-1,j),longestCommonSubseq(i,j-1));
        }
        return dp[i][j];
    }

    public static void main(String[] args) {
        LCSPrac lcs = new LCSPrac();
        System.out.println("s1 length= "+lcs.s1.length());
        System.out.println("s2 length= "+lcs.s2.length());
        System.out.println("ans= "+lcs.longestCommonSubsequence());
        System.out.println("count= "+lcs.count);
    }
}
